import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

//O AuthService vai conter a logica necessaria para gerenciar a autenticação do usuario
@Injectable({
  providedIn: 'root'
})
export class AuthService {
  //Vai rastrear se o usuario esta logado ou não na aplicação
  private isAuthenticated = false;

  //Vai armazenar qual o tipo de usuario está acessando a aplicação, se é "Admin" ou "User"
  private userRole: string | null = null;

  constructor(private http: HttpClient, private router: Router) {}


  login(email: string, password: string) {
    const loginData = 
    {
      email: email,
      senha: password
    }
    return this.http.post<{username: string, email: string, role: string}>('https://localhost:7187/api/usuario/login', loginData)
      .subscribe
      ({
        next: response => 
        {
          this.isAuthenticated = true;
          this.userRole = response.role;
          localStorage.setItem('userRole', this.userRole);
          this.router.navigate(['/home']);
        }, 
        error: error => 
        {
          alert(error.error);
        }
      });
  }

  logout() {
    this.isAuthenticated = false;
    this.userRole = null;
    localStorage.removeItem('userRole');
    this.router.navigate(['/login']);
  }

  isAdmin() {
    return this.userRole === 'Admin';
  }

  isUser() {
    return this.userRole === 'User';
  }

  get getAuthenticated(): boolean {
    return this.isAuthenticated;
  }
}
