import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FilmeModel } from 'src/app/models/filme.model';
import { GeneroModel } from 'src/app/models/genero.model';

@Component({
  selector: 'app-cadastrar',
  templateUrl: './cadastrar.component.html',
  styleUrls: ['./cadastrar.component.css']
})
export class CadastrarComponent {
  constructor(private client: HttpClient) {}

  //Criando as variaveis que vão conter os dados do formulario
  nome: string = "";
  classif_ind: number = 0;
  ano_lanc: number = 0;
  alugado: boolean = false;
  GeneroId: number = 0;
  Genero: GeneroModel[] = [];

  //Função "Cadastrar"
  cadastrar(): void
  {
    //Objeto carregado com os dados do formulario
    //Vai ser enviado para a API
    let filme: FilmeModel = {
      nome: this.nome,
      classif_ind: this.classif_ind,
      ano_lanc: this.ano_lanc,
      alugado: this.alugado,
      GeneroId: this.GeneroId
    }

    //Faz uma requisção para a API, e envia o objeto "filme"
    this.client.post<FilmeModel>
    ("https://localhost:7187/api/filme/cadastrar", filme)
    .subscribe
    ({
      next: (filme) => {
        console.log("Filme cadastrado com sucesso", filme);
      },
      error: (erro) => {
        console.log(erro);
      }
    })
  }
}
