import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { UsuarioModel } from 'src/app/models/usuario.model';

@Component({
  selector: 'app-login-usuario',
  templateUrl: './login-usuario.component.html',
  styleUrls: ['./login-usuario.component.css']
})
export class LoginUsuarioComponent {
  constructor(private http: HttpClient, private router: Router) {}

  senha: string = ""
  email: string = ""

  login(): void
  {
    const loginData = {
      email: this.email,
      senha: this.senha
    };

    this.http.post<UsuarioModel>
    ('https://localhost:7187/api/usuario/login', loginData)
    .subscribe
    ({
      next: () =>
        {
          console.log("Login realizado com sucesso")
          this.router.navigate(["pages/filme/listar"])
        },
        error: (erro) =>
        {
          console.log(erro)
        }
    })
  }

  //Messagem de erro caso o login seja malsucedido
  mensagemErro: string | null = null;

  onSubmit(form: NgForm) {
    if (form.valid) {
      // Simulação de chamada ao serviço de autenticação
      this.handleLoginFailure('Email ou senha inválidos');
    }
  }

  handleLoginFailure(message: string) {
    this.mensagemErro = message;
    // O alerta desaparecerá após 3 segundos devido à animação CSS
  }
}
